div.code:
.card .image-container img {
    width: 100%; /* Asigură că imaginea ocupă întreaga lățime a containerului */
    display: block; /* Elimină spațiile albe de la margini */
    border: none; /* Elimină orice bordură */
    margin: 0; /* Elimină marginile */
    padding: 0; /* Elimină orice padding */
}

.bordered-img {
    /* Dacă ai alt stil specific pentru imagini cu bordură, poți anula acel stil aici */
    border: none; /* Asigură că nu există bordură */
}

