div.code:
.card {
    background-color: rgb(206, 187, 216); /* Mov deschis */
    padding: 20px; /* Spațiu interior pentru card */
    margin-bottom: 20px; /* Spațiu între carduri */
    border-radius: 8px; /* Rotunjire colțuri */
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1); /* Umbră sub card */
}

.card-content {
    padding: 10px; /* Spațiu interior pentru conținutul cardului */
}

